import React, { useEffect, useState } from "react";
import { Button, Box, Typography, Grid, Card, CardContent } from "@mui/material";
import api from "../../services/api";
import NoteItem from "./NoteItem";

const Dashboard: React.FC = () => {
  interface Note {
    _id: string;
    title: string;
    content: string;
  }

  const [notes, setNotes] = useState<Note[]>([]);

  const fetchNotes = async () => {
    try {
      const response = await api.get("/notes");
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  const handleDelete = (id: string) => {
    setNotes(notes.filter(note => note._id !== id));
  };

  return (
    <Box
      sx={{
        p: 2,
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        height: "100vh",
        backgroundColor: "#f9f9f9",
      }}
    >
      {/* Top Bar */}
      <Box
        sx={{
          width: "100%",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          mb: 2,
        }}
      >
        <Typography variant="h6" sx={{ fontWeight: "bold" }}>
          Dashboard
        </Typography>
        <Button variant="text" color="primary" href="/logout">
          Sign Out
        </Button>
      </Box>

      {/* Main Content */}
      <Box
        sx={{
          width: "100%",
          maxWidth: 1200,
          display: "flex",
          flexDirection: { xs: "column", md: "row" },
          gap: 3,
        }}
      >
        {/* Welcome Section */}
        <Card
          sx={{
            flex: 1,
            p: 2,
            textAlign: "center",
            boxShadow: 3,
            backgroundColor: "#ffffff",
          }}
        >
          <CardContent>
            <Typography variant="h5" fontWeight="bold">
              Welcome, Jonas Kahnwald!
            </Typography>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
              Email: xxxxxx@xxxx.com
            </Typography>
            <Button
              variant="contained"
              color="primary"
              href="/create-note"
              sx={{
                py: 1.5,
                px: 4,
                fontWeight: "bold",
                mt: 2,
              }}
            >
              Create Note
            </Button>
          </CardContent>
        </Card>

        {/* Notes Section */}
        <Box
          sx={{
            flex: 2,
            p: 2,
            boxShadow: 3,
            borderRadius: 2,
            backgroundColor: "#ffffff",
            maxHeight: "70vh",
            overflowY: "auto",
          }}
        >
          <Typography variant="h6" gutterBottom>
            Your Notes
          </Typography>
          <Grid container spacing={2}>
            {notes.map(note => (
              <Grid item xs={12} sm={6} key={note._id}>
                <NoteItem note={note} onDelete={handleDelete} />
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
