import React, { useState } from "react";
import {
  Container,
  Grid,
  Box,
  TextField,
  Button,
  Typography,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { useFormik } from "formik";
import * as Yup from "yup";
import api from "../../services/api"; // Importing the API service

const Login: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [useOtp, setUseOtp] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  const validationSchema = Yup.object({
    email: Yup.string().email("Invalid email").required("Required"),
    password: Yup.string().when("useOtp", {
      is: false,
      then: Yup.string().required("Required"),
    }),
    otp: Yup.string().when("useOtp", {
      is: true,
      then: Yup.string().required("Required"),
    }),
  });

  const handleVerifyOtp = async () => {
    try {
      const { email, otp } = formik.values;

      // API call to verify OTP
      const response = await api.post("/auth/verify-otp", { email, otp });

      alert(response.data.message); // "User registered successfully"
      // Set login state and redirect
      setIsLoggedIn(true);
      navigate("/notes"); // Navigate to the notes page
    } catch (error: any) {
      alert(error.response?.data?.message || "Invalid OTP");
    }
  };

  const handleLogin = async () => {
    try {
      const { email, password } = formik.values;

      // API call to login with email and password
      const response = await api.post("/auth/login", { email, password });

      alert(response.data.message); // "Login successful"
      // Set login state and redirect
      setIsLoggedIn(true);
      navigate("/notes"); // Navigate to the notes page
    } catch (error: any) {
      alert(error.response?.data?.message || "Invalid credentials");
    }
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    if (useOtp) {
      await handleVerifyOtp();
    } else {
      await handleLogin();
    }
  };

  const formik = useFormik({
    initialValues: { email: "", password: "", otp: "" },
    validationSchema,
    onSubmit: handleSubmit,
  });

  const handleToggleLoginMethod = () => {
    setUseOtp(!useOtp);
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: "100%",
          padding: 0,
          boxShadow: 3,
          borderRadius: 3,
          overflow: "hidden",
        }}
      >
        <Grid item xs={12} md={6} sx={{ padding: 4 }}>
          <Box
            component="form"
            noValidate
            autoComplete="off"
            onSubmit={formik.handleSubmit}
            sx={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignItems: "flex-start",
              gap: "20px",
            }}
          >
            <TextField
              label="Email"
              name="email"
              fullWidth
              margin="normal"
              variant="outlined"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={formik.touched.email && Boolean(formik.errors.email)}
              helperText={formik.touched.email && formik.errors.email}
            />
            {!useOtp && (
              <TextField
                label="Password"
                name="password"
                type={showPassword ? "text" : "password"}
                fullWidth
                margin="normal"
                variant="outlined"
                value={formik.values.password}
                onChange={formik.handleChange}
                error={formik.touched.password && Boolean(formik.errors.password)}
                helperText={formik.touched.password && formik.errors.password}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton onClick={() => setShowPassword(!showPassword)}>
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            )}
            {useOtp && (
              <TextField
                label="OTP"
                name="otp"
                fullWidth
                margin="normal"
                variant="outlined"
                value={formik.values.otp}
                onChange={formik.handleChange}
                error={formik.touched.otp && Boolean(formik.errors.otp)}
                helperText={formik.touched.otp && formik.errors.otp}
              />
            )}
            <Button
              type="submit"
              variant="contained"
              fullWidth
              sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF", borderRadius: "10px" }}
            >
              {useOtp ? "Verify OTP" : "Login"}
            </Button>
          </Box>
          <Typography
            align="center"
            variant="body2"
            sx={{ cursor: "pointer" }}
            onClick={handleToggleLoginMethod}
          >
            {useOtp ? "Login with Password" : "Login with OTP"}
          </Typography>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
