import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  IconButton,
  TextField,
} from "@mui/material";
import { Delete, Edit, Save } from "@mui/icons-material";
import api from "../../services/api";

const Dashboard: React.FC = () => {
  interface Note {
    _id: string;
    title: string;
    content: string;
    isEditing?: boolean; // Added to track edit mode
  }

  const [notes, setNotes] = useState<Note[]>([]);

  const fetchNotes = async () => {
    try {
      const response = await api.get("/notes");
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await api.delete(`/notes/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const handleEditToggle = (id: string) => {
    setNotes(
      notes.map((note) =>
        note._id === id ? { ...note, isEditing: !note.isEditing } : note
      )
    );
  };

  const handleSave = async (id: string, updatedTitle: string, updatedContent: string) => {
    try {
      await api.put(`/notes/${id}`, { title: updatedTitle, content: updatedContent });
      setNotes(
        notes.map((note) =>
          note._id === id
            ? { ...note, title: updatedTitle, content: updatedContent, isEditing: false }
            : note
        )
      );
    } catch (error) {
      console.error("Error saving note:", error);
    }
  };

  const truncateContent = (content: string, maxLength: number = 15) =>
    content.length > maxLength ? `${content.slice(0, maxLength)}..` : content;

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <Box
      sx={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Background Image */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url(/assets/images/bg-img.png)",
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.9,
          zIndex: -5,
        }}
      />

      {/* Main Content */}
      <Box
        sx={{
          p: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          height: "100%",
          backgroundColor: "rgba(255, 255, 255, 0.5)",
        }}
      >
        {/* Top Bar */}
        <Box
          sx={{
            width: "100%",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            mb: 2,
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            Dashboard
          </Typography>
          <Button variant="text" color="primary" href="/logout">
            Sign Out
          </Button>
        </Box>

        {/* Notes List */}
        <Box
          sx={{
            width: "100%",
            maxWidth: 500,
            flexGrow: 1,
            overflowY: "auto",
          }}
        >
          <Typography variant="h6" gutterBottom>
            Notes
          </Typography>
          <Grid container spacing={2}>
            {notes.map((note) => (
              <Grid item xs={12} key={note._id}>
                <Card
                  sx={{
                    p: 2,
                    boxShadow: 1,
                    borderRadius: "8px",
                    backgroundColor: "#fff",
                  }}
                >
                  {note.isEditing ? (
                    // Edit Mode
                    <Box>
                      <TextField
                        fullWidth
                        margin="normal"
                        label="Title"
                        defaultValue={note.title}
                        onChange={(e) =>
                          setNotes(
                            notes.map((n) =>
                              n._id === note._id
                                ? { ...n, title: e.target.value }
                                : n
                            )
                          )
                        }
                      />
                      <TextField
                        fullWidth
                        margin="normal"
                        label="Content"
                        defaultValue={note.content}
                        multiline
                        rows={4}
                        onChange={(e) =>
                          setNotes(
                            notes.map((n) =>
                              n._id === note._id
                                ? { ...n, content: e.target.value }
                                : n
                            )
                          )
                        }
                      />
                      <IconButton
                        aria-label="save"
                        onClick={() =>
                          handleSave(note._id, note.title, note.content)
                        }
                        sx={{
                          color: "green",
                        }}
                      >
                        <Save />
                      </IconButton>
                    </Box>
                  ) : (
                    // View Mode
                    <Box>
                      <Typography variant="subtitle1" fontWeight="bold">
                        {note.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {note.content}
                      </Typography>
                      <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
                        <IconButton
                          aria-label="edit"
                          onClick={() => handleEditToggle(note._id)}
                        >
                          <Edit />
                        </IconButton>
                        <IconButton
                          aria-label="delete"
                          onClick={() => handleDelete(note._id)}
                          sx={{ color: "red" }}
                        >
                          <Delete />
                        </IconButton>
                      </Box>
                    </Box>
                  )}
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
