import React, { useState } from "react";
import {
  Container,
  Grid,
  Typography,
  Box,
  TextField,
  Button,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import api from "../../services/api";

const Login: React.FC = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [loginWithOtp, setLoginWithOtp] = useState(false);

  const togglePasswordVisibility = () => setShowPassword(!showPassword);

  const initialValues = { email: "", password: "", otp: "" };

  const validationSchema = Yup.object({
    email: Yup.string().email("Invalid email").required("Required"),
    password: !loginWithOtp
      ? Yup.string().required("Password is required")
      : Yup.string(),
    otp: loginWithOtp
      ? Yup.string().required("OTP is required")
      : Yup.string(),
  });

  const handleSubmit = async (values: typeof initialValues) => {
    try {
      if (loginWithOtp) {
        const response = await api.post("/auth/verify-otp", {
          email: values.email,
          otp: values.otp,
        });
        localStorage.setItem("token", response.data.token);
      } else {
        const response = await api.post("/auth/login", {
          email: values.email,
          password: values.password,
        });
        localStorage.setItem("token", response.data.token);
      }
      window.location.href = "/notes";
    } catch (error: any) {
      alert(error.response?.data?.message || "Login failed");
    }
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: "100%",
          boxShadow: 3,
          borderRadius: 3,
          overflow: "hidden",
        }}
      >
        {/* Left Side - Image */}
        <Grid
          item
          xs={12}
          md={7}
          sx={{
            backgroundImage: "url('/assets/images/signup-bg.png')",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        ></Grid>

        {/* Right Side - Login Form */}
        <Grid item xs={12} md={5} sx={{ padding: 4 }}>
          <Box sx={{ padding: 9 }}>
            <Typography variant="h4" fontWeight="bold" gutterBottom>
              Login
            </Typography>
            <Typography variant="body1" color="text.secondary" mb={4}>
              Login to access your account
            </Typography>
            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ errors, touched }) => (
                <Form>
                  <Field
                    name="email"
                    as={TextField}
                    label="Email"
                    fullWidth
                    margin="normal"
                    variant="outlined"
                    error={touched.email && !!errors.email}
                    helperText={touched.email && errors.email}
                  />
                  {loginWithOtp ? (
                    <Field
                      name="otp"
                      as={TextField}
                      label="OTP"
                      fullWidth
                      margin="normal"
                      variant="outlined"
                      error={touched.otp && !!errors.otp}
                      helperText={touched.otp && errors.otp}
                    />
                  ) : (
                    <Field
                      name="password"
                      as={TextField}
                      label="Password"
                      type={showPassword ? "text" : "password"}
                      fullWidth
                      margin="normal"
                      variant="outlined"
                      error={touched.password && !!errors.password}
                      helperText={touched.password && errors.password}
                      InputProps={{
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton onClick={togglePasswordVisibility}>
                              {showPassword ? <VisibilityOff /> : <Visibility />}
                            </IconButton>
                          </InputAdornment>
                        ),
                      }}
                    />
                  )}
                  <Button
                    type="submit"
                    variant="contained"
                    fullWidth
                    sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF" }}
                  >
                    {loginWithOtp ? "Login with OTP" : "Login"}
                  </Button>
                </Form>
              )}
            </Formik>
            <Typography
              variant="body2"
              align="center"
              sx={{ cursor: "pointer", color: "primary.main" }}
              onClick={() => setLoginWithOtp(!loginWithOtp)}
            >
              {loginWithOtp
                ? "Login with Password"
                : "Login with OTP instead"}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
