import React, { useState } from "react";
import {
  Box,
  Button,
  TextField,
  Typography,
  Grid,
  IconButton,
  InputAdornment,
  CircularProgress,
} from "@mui/material";
import { Visibility, VisibilityOff, CalendarToday } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const Signup: React.FC = () => {
  const [formData, setFormData] = useState({
    name: "",
    date_of_birth: "",
    email: "",
    otp: "",
    password: "",
  });
  const [showOtpField, setShowOtpField] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // Handle form field changes
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Handle OTP visibility toggle
  const handleOtpVisibility = () => setShowPassword(!showPassword);

  // Send signup request
  const handleSignup = async () => {
    try {
      setLoading(true);
      const { name, email, date_of_birth, password } = formData;

      // API Call for signup
      const response = await axios.post("/api/auth/signup", {
        name,
        email,
        date_of_birth,
        password,
      });

      setOtpSent(true);
      setLoading(false);
      alert(response.data.message); // "OTP sent to email. Please verify to complete signup."
    } catch (error: any) {
      setLoading(false);
      alert(error.response?.data?.message || "Something went wrong");
    }
  };

  // Handle OTP verification
  const handleVerifyOtp = async () => {
    try {
      setLoading(true);
      const { email, otp } = formData;

      // API Call for verifying OTP
      const response = await axios.post("/api/auth/verify-signup-otp", { email, otp });

      setLoading(false);
      alert(response.data.message); // "User registered successfully"
      navigate("/login"); // Redirect to login page
    } catch (error: any) {
      setLoading(false);
      alert(error.response?.data?.message || "Invalid OTP");
    }
  };

  return (
    <Box
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "url('/assets/images/signup-bg.png') no-repeat center center",
        backgroundSize: "cover",
      }}
    >
      <Box
        sx={{
          width: "100%",
          maxWidth: "500px",
          padding: "32px",
          background: "rgba(255, 255, 255, 0.9)",
          borderRadius: "8px",
          boxShadow: 3,
        }}
      >
        <Typography variant="h4" fontWeight="bold" gutterBottom>
          Sign up
        </Typography>
        <Typography variant="body1" color="text.secondary" mb={4}>
          Sign up to enjoy the feature of HD
        </Typography>

        {/* Form Fields */}
        <TextField
          label="Your Name"
          name="name"
          fullWidth
          margin="normal"
          variant="outlined"
          value={formData.name}
          onChange={handleChange}
        />
        <TextField
          label="Date of Birth"
          name="date_of_birth"
          fullWidth
          margin="normal"
          variant="outlined"
          value={formData.date_of_birth}
          onChange={handleChange}
          InputProps={{
            endAdornment: (
              <InputAdornment position="end">
                <CalendarToday />
              </InputAdornment>
            ),
          }}
        />
        <TextField
          label="Email"
          name="email"
          fullWidth
          margin="normal"
          variant="outlined"
          value={formData.email}
          onChange={handleChange}
        />
        {otpSent ? (
          <TextField
            label="OTP"
            name="otp"
            fullWidth
            margin="normal"
            variant="outlined"
            value={formData.otp}
            onChange={handleChange}
            type={showPassword ? "text" : "password"}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton onClick={handleOtpVisibility}>
                    {showPassword ? <VisibilityOff /> : <Visibility />}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
        ) : (
          <TextField
            label="Password"
            name="password"
            fullWidth
            margin="normal"
            variant="outlined"
            type="password"
            value={formData.password}
            onChange={handleChange}
          />
        )}

        {/* Action Button */}
        <Button
          variant="contained"
          fullWidth
          sx={{ marginY: 2, paddingY: 1.5 }}
          onClick={otpSent ? handleVerifyOtp : handleSignup}
          disabled={loading}
        >
          {loading ? <CircularProgress size={24} /> : otpSent ? "Verify OTP" : "Sign up"}
        </Button>

        {/* Navigate to Login */}
        <Typography align="center" variant="body2">
          Already have an account?{" "}
          <Typography
            component="span"
            color="primary"
            sx={{ cursor: "pointer" }}
            onClick={() => navigate("/login")}
          >
            Sign in
          </Typography>
        </Typography>
      </Box>
    </Box>
  );
};

export default Signup;
