import React, { useState } from "react";
import {
  Container,
  Grid,
  Typography,
  Box,
  TextField,
  InputAdornment,
  IconButton,
  Button,
  useMediaQuery,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { useFormik } from "formik";
import * as Yup from "yup";
import api from "../../services/api";

const Login: React.FC = () => {
  const [useOtp, setUseOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const isMobile = useMediaQuery("(max-width:600px)"); // For detecting mobile view

  const handleToggleLoginMethod = () => {
    setUseOtp(!useOtp);
    setOtpSent(false);
  };

  const handleSendOtp = async (email: string) => {
    try {
      await api.post("/auth/send-otp", { email });
      alert("OTP sent successfully to your email");
      setOtpSent(true);
    } catch (error: any) {
      alert(error.response?.data?.message || "Failed to send OTP");
    }
  };

  const validationSchema = Yup.object({
    email: Yup.string().email("Invalid email").required("Email is required"),
    ...(useOtp
      ? { otp: Yup.string().required("OTP is required") }
      : { password: Yup.string().required("Password is required") }),
  });

  const formik = useFormik({
    initialValues: { email: "", password: "", otp: "" },
    validationSchema,
    onSubmit: async (values) => {
      try {
        if (useOtp) {
          const response = await api.post("/auth/verify-otp", {
            email: values.email,
            otp: values.otp,
          });
          localStorage.setItem("token", response.data.token);
        } else {
          const response = await api.post("/auth/login", {
            email: values.email,
            password: values.password,
          });
          localStorage.setItem("token", response.data.token);
        }
        window.location.href = "/notes";
      } catch (error: any) {
        alert(error.response?.data?.message || "Login failed");
      }
    },
  });

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        padding: isMobile ? 2 : 0, // Add padding for mobile
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: isMobile ? "auto" : "100%",
          boxShadow: isMobile ? "none" : 3,
          borderRadius: isMobile ? 0 : 3,
          overflow: "hidden",
        }}
      >
        {!isMobile && (
          <Grid
            item
            xs={12}
            md={7}
            sx={{
              backgroundImage: "url('/assets/images/signup-bg.png')",
              backgroundSize: "cover",
              backgroundPosition: "center",
            }}
          ></Grid>
        )}

        <Grid
          item
          xs={12}
          md={5}
          sx={{
            padding: isMobile ? 3 : 4,
            display: "flex",
            flexDirection: "column",
            alignItems: isMobile ? "center" : "flex-start",
            justifyContent: "center",
          }}
        >
          <Box sx={{ display: "flex", alignItems: "center", marginBottom: 3 }}>
            <img
              src="/assets/images/hd-icon.png"
              alt="HD Icon"
              style={{
                width: 32,
                height: 32,
                marginRight: 8,
                display: isMobile ? "none" : "block",
              }}
            />
            <Typography
              variant="h6"
              fontWeight="bold"
              sx={{
                color: "#232323",
                fontFeatureSettings: "'liga' off, 'clig' off",
                fontFamily: "Inter",
                fontSize: isMobile ? "20px" : "24px",
                fontWeight: 600,
                letterSpacing: "-0.96px",
              }}
            >
              HD
            </Typography>
          </Box>
          <Box
            component="form"
            noValidate
            onSubmit={formik.handleSubmit}
            sx={{
              width: isMobile ? "100%" : "auto",
            }}
          >
            <Typography
              variant="h5"
              fontWeight="bold"
              gutterBottom
              align={isMobile ? "center" : "left"}
            >
              Sign up
            </Typography>
            <Typography
              variant="body2"
              color="text.secondary"
              mb={4}
              align={isMobile ? "center" : "left"}
            >
              Sign up to enjoy the feature of HD
            </Typography>
            <TextField
              label="Your Name"
              name="name"
              fullWidth
              margin="normal"
              variant="outlined"
            />
            <TextField
              label="Date of Birth"
              name="dob"
              fullWidth
              margin="normal"
              variant="outlined"
            />
            <TextField
              label="Email"
              name="email"
              fullWidth
              margin="normal"
              variant="outlined"
              value={formik.values.email}
              onChange={formik.handleChange}
              error={formik.touched.email && !!formik.errors.email}
              helperText={formik.touched.email && formik.errors.email}
            />
            <TextField
              label="OTP"
              name="otp"
              fullWidth
              margin="normal"
              variant="outlined"
              value={formik.values.otp}
              onChange={formik.handleChange}
            />
            <Button
              type="submit"
              variant="contained"
              fullWidth
              sx={{
                marginY: 2,
                paddingY: 1.5,
                backgroundColor: "#367AFF",
                borderRadius: "10px",
              }}
            >
              Sign up
            </Button>
            <Typography
              align="center"
              variant="body2"
              sx={{ cursor: "pointer" }}
              onClick={handleToggleLoginMethod}
            >
              Already have an account? Sign in
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
