import React, { useState } from "react";
import {
  Container,
  Grid,
  Typography,
  Box,
  TextField,
  Button,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import { Formik, Form, Field } from "formik";
import * as Yup from "yup";
import api from "../../services/api";

const Login: React.FC = () => {
  const [useOtp, setUseOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  const initialValues = { email: "", password: "", otp: "" };

  const validationSchema = Yup.object({
    email: Yup.string().email("Invalid email").required("Required"),
    password: Yup.string().when("otp", {
      is: (otp) => !otp,
      then: Yup.string().required("Password is required"),
    }),
    otp: Yup.string().when("password", {
      is: (password) => !password,
      then: Yup.string().required("OTP is required"),
    }),
  });

  const handleSendOtp = async (email: string) => {
    try {
      await api.post("/auth/send-otp", { email });
      alert("OTP sent successfully");
      setOtpSent(true);
    } catch (error: any) {
      alert(error.response?.data?.message || "Failed to send OTP");
    }
  };

  const handleSubmit = async (values: typeof initialValues) => {
    try {
      if (useOtp) {
        // OTP-based login
        const response = await api.post("/auth/verify-otp", {
          email: values.email,
          otp: values.otp,
        });
        localStorage.setItem("token", response.data.token);
      } else {
        // Password-based login
        const response = await api.post("/auth/login", {
          email: values.email,
          password: values.password,
        });
        localStorage.setItem("token", response.data.token);
      }
      window.location.href = "/notes";
    } catch (error: any) {
      alert(error.response?.data?.message || "Login failed");
    }
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: "100%",
          boxShadow: 3,
          borderRadius: 3,
          overflow: "hidden",
        }}
      >
        {/* Left Side - Image */}
        <Grid
          item
          xs={12}
          md={6}
          sx={{
            backgroundImage: "url('/assets/images/signup-bg.png')",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        ></Grid>

        {/* Right Side - Login Form */}
        <Grid item xs={12} md={6} sx={{ padding: 4 }}>
          <Box component="div" noValidate autoComplete="off" sx={{ padding: 9 }}>
            <Typography variant="h4" fontWeight="bold" gutterBottom>
              Login
            </Typography>
            <Typography variant="body1" color="text.secondary" mb={4}>
              Login with your email and password or OTP
            </Typography>
            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ errors, touched, values }) => (
                <Form>
                  <Field
                    name="email"
                    as={TextField}
                    label="Email"
                    fullWidth
                    margin="normal"
                    error={touched.email && !!errors.email}
                    helperText={touched.email && errors.email}
                  />

                  {useOtp ? (
                    <TextField
                      label="OTP"
                      name="otp"
                      fullWidth
                      margin="normal"
                      value={values.otp}
                      onChange={(e) => (values.otp = e.target.value)}
                      error={touched.otp && !!errors.otp}
                      helperText={touched.otp && errors.otp}
                    />
                  ) : (
                    <TextField
                      label="Password"
                      name="password"
                      type={showPassword ? "text" : "password"}
                      fullWidth
                      margin="normal"
                      value={values.password}
                      onChange={(e) => (values.password = e.target.value)}
                      error={touched.password && !!errors.password}
                      helperText={touched.password && errors.password}
                      InputProps={{
                        endAdornment: (
                          <InputAdornment position="end">
                            <IconButton
                              onClick={() => setShowPassword(!showPassword)}
                            >
                              {showPassword ? <VisibilityOff /> : <Visibility />}
                            </IconButton>
                          </InputAdornment>
                        ),
                      }}
                    />
                  )}

                  {useOtp && !otpSent && (
                    <Button
                      variant="outlined"
                      fullWidth
                      sx={{ marginY: 1.5 }}
                      onClick={() => handleSendOtp(values.email)}
                    >
                      Send OTP
                    </Button>
                  )}

                  <Button
                    type="submit"
                    variant="contained"
                    fullWidth
                    sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF" }}
                  >
                    {useOtp ? "Login with OTP" : "Login"}
                  </Button>
                </Form>
              )}
            </Formik>
            <Typography
              variant="body2"
              align="center"
              sx={{ cursor: "pointer", marginTop: 2 }}
              onClick={() => setUseOtp(!useOtp)}
            >
              {useOtp ? "Login with Password" : "Login with OTP"}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
