import React, { useState } from "react";
import {
  Container,
  Grid,
  Typography,
  Box,
  TextField,
  Button,
  InputAdornment,
  IconButton,
} from "@mui/material";
import { Visibility, VisibilityOff } from "@mui/icons-material";
import api from "../../services/api";

const Login: React.FC = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    otp: "",
  });
  const [loginWithOtp, setLoginWithOtp] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [otpSent, setOtpSent] = useState(false);

  // Handle form input changes
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Toggle password visibility
  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  // Handle login with password
  const handleLogin = async () => {
    try {
      const { email, password } = formData;
      const response = await api.post("/auth/login", { email, password });
      localStorage.setItem("token", response.data.token);
      window.location.href = "/notes";
    } catch (error) {
      alert(error.response?.data?.message || "Login failed");
    }
  };

  // Handle sending OTP
  const handleSendOtp = async () => {
    try {
      const { email } = formData;
      const response = await api.post("/auth/send-otp", { email });
      alert(response.data.message); // "OTP sent successfully"
      setOtpSent(true);
    } catch (error) {
      alert(error.response?.data?.message || "Failed to send OTP");
    }
  };

  // Handle OTP verification
  const handleVerifyOtp = async () => {
    try {
      const { email, otp } = formData;
      const response = await api.post("/auth/verify-otp", { email, otp });
      localStorage.setItem("token", response.data.token);
      alert(response.data.message); // "OTP verified successfully"
      window.location.href = "/notes";
    } catch (error) {
      alert(error.response?.data?.message || "Invalid OTP");
    }
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: "100%",
          boxShadow: 3,
          borderRadius: 3,
          overflow: "hidden",
        }}
      >
        {/* Left Side - Image */}
        <Grid
          item
          xs={12}
          md={7}
          sx={{
            backgroundImage: "url('/assets/images/signup-bg.png')",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        ></Grid>

        {/* Right Side - Login Form */}
        <Grid item xs={12} md={5} sx={{ padding: 4 }}>
          <Typography
            variant="h6"
            fontWeight="bold"
            sx={{
              color: "#232323",
              fontFeatureSettings: "'liga' off, 'clig' off",
              fontFamily: "Inter",
              fontSize: "24px",
              fontStyle: "normal",
              fontWeight: 600,
              lineHeight: "110%", // 26.4px
              letterSpacing: "-0.96px",
            }}
          >
            HD
          </Typography>
          <Box component="form" noValidate autoComplete="off" sx={{ padding: 9 }}>
            <Typography variant="h4" fontWeight="bold" gutterBottom>
              Login
            </Typography>
            <Typography variant="body1" color="text.secondary" mb={4}>
              Login to access your account
            </Typography>
            <TextField
              label="Email"
              name="email"
              fullWidth
              margin="normal"
              variant="outlined"
              type="email"
              value={formData.email}
              onChange={handleChange}
            />

            {loginWithOtp ? (
              otpSent ? (
                <TextField
                  label="OTP"
                  name="otp"
                  fullWidth
                  margin="normal"
                  variant="outlined"
                  value={formData.otp}
                  onChange={handleChange}
                />
              ) : (
                <Button
                  variant="contained"
                  fullWidth
                  sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF" }}
                  onClick={handleSendOtp}
                >
                  Send OTP
                </Button>
              )
            ) : (
              <TextField
                label="Password"
                name="password"
                fullWidth
                margin="normal"
                variant="outlined"
                type={showPassword ? "text" : "password"}
                value={formData.password}
                onChange={handleChange}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton onClick={handlePasswordVisibility}>
                        {showPassword ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            )}

            <Button
              variant="contained"
              fullWidth
              sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF" }}
              onClick={loginWithOtp ? handleVerifyOtp : handleLogin}
            >
              {loginWithOtp ? (otpSent ? "Verify OTP" : "Send OTP") : "Login"}
            </Button>

            <Typography
              align="center"
              variant="body2"
              color="primary"
              sx={{ cursor: "pointer" }}
              onClick={() => setLoginWithOtp(!loginWithOtp)}
            >
              {loginWithOtp ? "Login with Password" : "Login with OTP"}
            </Typography>
          </Box>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Login;
