import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  IconButton,
  TextField,
  Collapse,
} from "@mui/material";
import { Delete, Edit, Save, Cancel, ExpandMore, ExpandLess } from "@mui/icons-material";
import api from "../../services/api";

const Dashboard: React.FC = () => {
  interface Note {
    _id: string;
    title: string;
    content: string;
  }

  const [notes, setNotes] = useState<Note[]>([]);
  const [expandedNote, setExpandedNote] = useState<string | null>(null);
  const [editingNote, setEditingNote] = useState<Note | null>(null);
  const [isCreatingNote, setIsCreatingNote] = useState(false);
  const [newNote, setNewNote] = useState({ title: "", content: "" });

  const fetchNotes = async () => {
    try {
      const response = await api.get("/notes");
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await api.delete(`/notes/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const handleEditStart = (note: Note) => {
    setEditingNote(note);
  };

  const handleEditCancel = () => {
    setEditingNote(null);
  };

  const handleEditSave = async () => {
    if (!editingNote) return;
    try {
      await api.put(`/notes/${editingNote._id}`, editingNote);
      setNotes((prevNotes) =>
        prevNotes.map((note) =>
          note._id === editingNote._id ? editingNote : note
        )
      );
      setEditingNote(null);
    } catch (error) {
      console.error("Error updating note:", error);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setEditingNote((prev) => (prev ? { ...prev, [name]: value } : null));
  };

  const handleCreateNoteToggle = () => {
    setIsCreatingNote((prev) => !prev);
    setNewNote({ title: "", content: "" });
  };

  const handleNewNoteChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setNewNote((prev) => ({ ...prev, [name]: value }));
  };

  const handleCreateNote = async () => {
    try {
      const response = await api.post("/notes", newNote);
      setNotes((prev) => [...prev, response.data]);
      setIsCreatingNote(false);
    } catch (error) {
      console.error("Error creating note:", error);
    }
  };

  const toggleExpand = (id: string) => {
    setExpandedNote((prev) => (prev === id ? null : id));
  };

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <Box
      sx={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Background */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url(/assets/images/bg-img.png)",
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.9,
          zIndex: -5,
        }}
      />

      {/* Content */}
      <Box
        sx={{
          p: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          height: "100%",
          backgroundColor: "rgba(255, 255, 255, 0.5)",
        }}
      >
        {/* Header */}
        <Box
          sx={{
            width: "100%",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            mb: 2,
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            Dashboard
          </Typography>
          <Button variant="text" color="primary" href="/logout">
            Sign Out
          </Button>
        </Box>

        {/* Welcome Card */}
        <Card
          sx={{
            width: "100%",
            maxWidth: 500,
            mb: 3,
            p: 2,
            textAlign: "center",
            boxShadow: 2,
            borderRadius: "10px",
          }}
        >
          <CardContent>
            <Typography variant="h6" fontWeight="bold">
              Welcome, Jonas Kahnwald!
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Email: xxxxxx@xxxx.com
            </Typography>
          </CardContent>
        </Card>

        {/* Create Note */}
        <Box sx={{ width: "100%", maxWidth: 500, mb: 3 }}>
          <Button
            variant="contained"
            onClick={handleCreateNoteToggle}
            sx={{
              width: "100%",
              py: 1.5,
              fontWeight: "bold",
              backgroundColor: "#367AFF",
              borderRadius: "10px",
            }}
          >
            {isCreatingNote ? "Cancel" : "Create Note"}
          </Button>

          {isCreatingNote && (
            <Card sx={{ mt: 2, p: 2, borderRadius: "8px", backgroundColor: "#fff", boxShadow: 1 }}>
              <TextField
                name="title"
                label="Title"
                value={newNote.title}
                onChange={handleNewNoteChange}
                fullWidth
                sx={{ mb: 2 }}
              />
              <TextField
                name="content"
                label="Content"
                value={newNote.content}
                onChange={handleNewNoteChange}
                multiline
                rows={4}
                fullWidth
              />
              <Button onClick={handleCreateNote} variant="contained" color="primary" sx={{ mt: 2 }}>
                Save Note
              </Button>
            </Card>
          )}
        </Box>

        {/* Notes List */}
        <Box sx={{ width: "100%", maxWidth: 500, flexGrow: 1, overflowY: "auto" }}>
          <Typography variant="h6" gutterBottom>
            Notes
          </Typography>
          <Grid container spacing={2}>
            {notes.map((note) => (
              <Grid item xs={12} key={note._id}>
                <Card
                  sx={{
                    boxShadow: 1,
                    borderRadius: "8px",
                    p: 1.5,
                    backgroundColor: "#fff",
                  }}
                >
                  <Box
                    onClick={() => toggleExpand(note._id)}
                    sx={{
                      cursor: "pointer",
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <Typography variant="subtitle1" fontWeight="bold">
                      {note.title}
                    </Typography>
                    <IconButton
                      onClick={(e) => {
                        e.stopPropagation();
                        toggleExpand(note._id);
                      }}
                    >
                      {expandedNote === note._id ? <ExpandLess /> : <ExpandMore />}
                    </IconButton>
                  </Box>
                  <Collapse in={expandedNote === note._id} timeout="auto" unmountOnExit>
                    <Box sx={{ mt: 1 }}>
                      <Typography variant="body2" sx={{ mb: 1 }}>
                        {note.content}
                      </Typography>
                      <Box sx={{ display: "flex", gap: 1 }}>
                        <IconButton onClick={() => handleEditStart(note)} color="primary">
                          <Edit />
                        </IconButton>
                        <IconButton onClick={() => handleDelete(note._id)} color="error">
                          <Delete />
                        </IconButton>
                      </Box>
                    </Box>
                  </Collapse>
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
