import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  IconButton,
  TextField,
} from "@mui/material";
import { Delete, Edit, Save, Cancel, ExpandMore, ExpandLess } from "@mui/icons-material";
import api from "../../services/api";

const Dashboard: React.FC = () => {
  interface Note {
    _id: string;
    title: string;
    content: string;
  }

  const [notes, setNotes] = useState<Note[]>([]);
  const [expandedNote, setExpandedNote] = useState<string | null>(null);
  const [editingNote, setEditingNote] = useState<Note | null>(null);

  const fetchNotes = async () => {
    try {
      const response = await api.get("/notes");
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await api.delete(`/notes/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  const handleEditStart = (note: Note) => {
    setEditingNote(note);
  };

  const handleEditCancel = () => {
    setEditingNote(null);
  };

  const handleEditSave = async () => {
    if (editingNote) {
      try {
        await api.put(`/notes/${editingNote._id}`, {
          title: editingNote.title,
          content: editingNote.content,
        });
        setNotes((prevNotes) =>
          prevNotes.map((note) =>
            note._id === editingNote._id ? editingNote : note
          )
        );
        setEditingNote(null);
      } catch (error) {
        console.error("Error updating note:", error);
      }
    }
  };

  const toggleExpand = (id: string) => {
    setExpandedNote((prev) => (prev === id ? null : id));
    setEditingNote(null); // Exit editing mode when toggling expansion
  };

  const handleInputChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>
  ) => {
    if (editingNote) {
      setEditingNote({ ...editingNote, [e.target.name]: e.target.value });
    }
  };

  useEffect(() => {
    fetchNotes();
  }, );

  return (
    <Box
      sx={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Background Image */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url(/assets/images/bg-img.png)",
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.9,
          zIndex: -5,
        }}
      />

      {/* Main Content */}
      <Box
        sx={{
          p: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          height: "100%",
          backgroundColor: "rgba(255, 255, 255, 0.5)",
        }}
      >
        {/* Top Bar */}
        <Box
          sx={{
            width: "100%",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            mb: 2,
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            Dashboard
          </Typography>
          <Button variant="text" color="primary" href="/logout">
            Sign Out
          </Button>
        </Box>

        {/* Welcome Card */}
        <Card
          sx={{
            width: "100%",
            maxWidth: 500,
            mb: 3,
            p: 2,
            textAlign: "center",
            boxShadow: 2,
            borderRadius: "10px",
          }}
        >
          <CardContent>
            <Typography variant="h6" fontWeight="bold">
              Welcome, Jonas Kahnwald!
            </Typography>
            <Typography variant="body2" color="text.secondary">
              Email: xxxxxx@xxxx.com
            </Typography>
          </CardContent>
        </Card>

        {/* Create Note Button */}
        <Button
          variant="contained"
          href="/create-note"
          sx={{
            width: "100%",
            maxWidth: 300,
            mb: 3,
            py: 1.5,
            fontWeight: "bold",
            backgroundColor: "#367AFF",
            borderRadius: "10px",
          }}
        >
          Create Note
        </Button>

        {/* Notes List */}
        <Box
          sx={{
            width: "100%",
            maxWidth: 500,
            flexGrow: 1,
            overflowY: "auto",
          }}
        >
          <Typography variant="h6" gutterBottom>
            Notes
          </Typography>
          <Grid container spacing={2}>
            {notes.map((note, index) => (
              <Grid item xs={12} key={note._id}>
                <Card
                  sx={{
                    boxShadow: 1,
                    borderRadius: "8px",
                    p: 1.5,
                    backgroundColor: "#fff",
                  }}
                >
                  <Box
                    onClick={() => toggleExpand(note._id)}
                    sx={{
                      cursor: "pointer",
                      display: "flex",
                      justifyContent: "space-between",
                      alignItems: "center",
                    }}
                  >
                    <Box>
                      <Typography variant="subtitle1" fontWeight="bold">
                        Note {index + 1}: {note.title}
                      </Typography>
                      <Typography variant="body2" color="text.secondary">
                        {expandedNote === note._id
                          ? note.content
                          : note.content.slice(0, 15) + "..."}
                      </Typography>
                    </Box>
                    <Box>
                      <IconButton>
                        {expandedNote === note._id ? (
                          <ExpandLess />
                        ) : (
                          <ExpandMore />
                        )}
                      </IconButton>
                    </Box>
                  </Box>

                  {expandedNote === note._id && (
                    <Box sx={{ mt: 1 }}>
                      {editingNote && editingNote._id === note._id ? (
                        <>
                          <TextField
                            name="title"
                            label="Title"
                            value={editingNote.title}
                            sx={{ mb: 2 }} // Adds spacing below the Title field
                            onChange={handleInputChange}
                            fullWidth
                          />
                          <TextField
                            name="content"
                            label="Content"
                            value={editingNote.content}
                            onChange={handleInputChange}
                            multiline
                            rows={4}
                            fullWidth
                          />
                          <Box
                            sx={{
                              display: "flex",
                              justifyContent: "space-between",
                              mt: 1,
                            }}
                          >
                            <Button
                              startIcon={<Save />}
                              onClick={handleEditSave}
                              variant="contained"
                              color="primary"
                            >
                              Save
                            </Button>
                            <Button
                              startIcon={<Cancel />}
                              onClick={handleEditCancel}
                              variant="outlined"
                              color="secondary"
                            >
                              Cancel
                            </Button>
                          </Box>
                        </>
                      ) : (
                        <Box sx={{ display: "flex", gap: 1, mt: 1 }}>
                          <IconButton
                            aria-label="edit"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleEditStart(note);
                            }}
                          >
                            <Edit />
                          </IconButton>
                          <IconButton
                            aria-label="delete"
                            onClick={(e) => {
                              e.stopPropagation();
                              handleDelete(note._id);
                            }}
                          >
                            <Delete />
                          </IconButton>
                        </Box>
                      )}
                    </Box>
                  )}
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
