import React, { useState } from "react";
import {
  Container,
  Grid,
  Typography,
  Box,
  TextField,
  InputAdornment,
  IconButton,
  Button,
} from "@mui/material";
import { CalendarToday, Visibility, VisibilityOff } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import api from "../../services/api"; // Importing the API service

const Signup: React.FC = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    trigger,
  } = useForm();

  const [formData, setFormData] = useState({
    name: "",
    date_of_birth: "",
    email: "",
    otp: "",
    password: "",
  });
  const [showOtp, setShowOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const navigate = useNavigate();

  // Handle form input changes
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Toggle OTP visibility
  const handleOtpVisibility = () => {
    setShowOtp(!showOtp);
  };

  // Send signup request
  const handleSignup = async () => {
    try {
      const { name, date_of_birth, email, password } = formData;

      // API call to send signup data
      const response = await api.post("/auth/signup", {
        name,
        date_of_birth,
        email,
        password,
      });

      alert(response.data.message); // "OTP sent to email. Please verify to complete signup."
      setOtpSent(true);
    } catch (error: any) {
      alert(error.response?.data?.message || "Something went wrong");
    }
  };

  // Verify OTP
  const handleVerifyOtp = async () => {
    try {
      const { email, otp } = formData;

      // API call to verify OTP
      const response = await api.post("/auth/verify-signup-otp", { email, otp });

      alert(response.data.message); // "User registered successfully"
      navigate("/login"); // Navigate to login page
    } catch (error: any) {
      alert(error.response?.data?.message || "Invalid OTP");
    }
  };

  return (
    <Container
      maxWidth={false}
      sx={{
        height: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Grid
        container
        spacing={2}
        sx={{
          height: "100%",
          boxShadow: 3,
          borderRadius: 3,
          overflow: "hidden",
        }}
      >
        {/* Left Side - Signup Form */}
        <Grid item xs={12} md={5} sx={{ padding: 4 }}>
          <Typography
            variant="h6"
            fontWeight="bold"
            sx={{
              color: "#232323",
              fontFeatureSettings: "'liga' off, 'clig' off",
              fontFamily: "Inter",
              fontSize: "24px",
              fontStyle: "normal",
              fontWeight: 600,
              lineHeight: "110%", // 26.4px
              letterSpacing: "-0.96px",
            }}
          >
            HD
          </Typography>
          <Box
            component="form"
            noValidate
            autoComplete="off"
            sx={{ padding: 9 }}
            onSubmit={handleSubmit(otpSent ? handleVerifyOtp : handleSignup)}
          >
            <Typography variant="h4" fontWeight="bold" gutterBottom>
              Sign up
            </Typography>
            <Typography variant="body1" color="text.secondary" mb={4}>
              Sign up to enjoy the feature of HD
            </Typography>
            <TextField
              label="Your Name"
              name="name"
              fullWidth
              margin="normal"
              variant="outlined"
              value={formData.name}
              {...register("name", {
                required: "Name is required",
                onBlur: () => trigger("name"),
              })}
              error={!!errors.name}
              helperText={errors.name?.message}
              onChange={handleChange}
            />
            <TextField
              label="Date of Birth"
              name="date_of_birth"
              fullWidth
              margin="normal"
              variant="outlined"
              value={formData.date_of_birth}
              {...register("date_of_birth", {
                required: "Date of Birth is required",
                onBlur: () => trigger("date_of_birth"),
              })}
              error={!!errors.date_of_birth}
              helperText={errors.date_of_birth?.message}
              onChange={handleChange}
              InputProps={{
                endAdornment: (
                  <InputAdornment position="end">
                    <CalendarToday />
                  </InputAdornment>
                ),
              }}
            />
            <TextField
              label="Email"
              name="email"
              fullWidth
              margin="normal"
              variant="outlined"
              type="email"
              value={formData.email}
              {...register("email", {
                required: "Email is required",
                pattern: {
                  value: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
                  message: "Enter a valid email address",
                },
                onBlur: () => trigger("email"),
              })}
              error={!!errors.email}
              helperText={errors.email?.message}
              onChange={handleChange}
            />
            {otpSent ? (
              <TextField
                label="OTP"
                name="otp"
                fullWidth
                margin="normal"
                variant="outlined"
                type={showOtp ? "text" : "password"}
                value={formData.otp}
                {...register("otp", {
                  required: "OTP is required",
                  onBlur: () => trigger("otp"),
                })}
                error={!!errors.otp}
                helperText={errors.otp?.message}
                onChange={handleChange}
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton onClick={handleOtpVisibility}>
                        {showOtp ? <VisibilityOff /> : <Visibility />}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            ) : (
              <TextField
                label="Password"
                name="password"
                fullWidth
                margin="normal"
                variant="outlined"
                type="password"
                value={formData.password}
                {...register("password", {
                  required: "Password is required",
                  minLength: {
                    value: 6,
                    message: "Password must be at least 6 characters",
                  },
                  onBlur: () => trigger("password"),
                })}
                error={!!errors.password}
                helperText={errors.password?.message}
                onChange={handleChange}
              />
            )}
            <Button
              variant="contained"
              fullWidth
              sx={{ marginY: 2, paddingY: 1.5, backgroundColor: "#367AFF" }}
              type="submit"
            >
              {otpSent ? "Verify OTP" : "Sign up"}
            </Button>
          </Box>
          <Typography align="center" variant="body2">
            Already have an account?{" "}
            <Typography
              component="span"
              color="primary"
              sx={{ cursor: "pointer" }}
              onClick={() => navigate("/login")}
            >
              Sign in
            </Typography>
          </Typography>
        </Grid>

        {/* Right Side - Image */}
        <Grid
          item
          xs={12}
          md={7}
          sx={{
            backgroundImage: "url('/assets/images/signup-bg.png')",
            backgroundSize: "cover",
            backgroundPosition: "center",
          }}
        ></Grid>
      </Grid>
    </Container>
  );
};

export default Signup;
