import React, { useEffect, useState } from "react";
import {
  Button,
  Box,
  Typography,
  Grid,
  Card,
  CardContent,
  IconButton,
  TextField,
} from "@mui/material";
import { Delete, Edit, Save } from "@mui/icons-material";
import api from "../../services/api";

const Dashboard: React.FC = () => {
  interface Note {
    _id: string;
    title: string;
    content: string;
  }

  const [notes, setNotes] = useState<Note[]>([]);
  const [editNoteId, setEditNoteId] = useState<string | null>(null); // Track the currently edited note
  const [editedContent, setEditedContent] = useState<{
    title: string;
    content: string;
  }>({ title: "", content: "" });

  // Fetch notes from API
  const fetchNotes = async () => {
    try {
      const response = await api.get("/notes");
      setNotes(response.data);
    } catch (error) {
      console.error("Error fetching notes:", error);
    }
  };

  // Handle deleting a note
  const handleDelete = async (id: string) => {
    try {
      await api.delete(`/notes/${id}`);
      setNotes(notes.filter((note) => note._id !== id));
    } catch (error) {
      console.error("Error deleting note:", error);
    }
  };

  // Handle editing a note
  const handleEdit = (note: Note) => {
    setEditNoteId(note._id); // Set the note ID to edit mode
    setEditedContent({ title: note.title, content: note.content }); // Pre-fill the fields
  };

  // Handle saving an edited note
  const handleSave = async (id: string) => {
    try {
      await api.put(`/notes/${id}`, editedContent); // Save the edited note
      setNotes((prevNotes) =>
        prevNotes.map((note) =>
          note._id === id ? { ...note, ...editedContent } : note
        )
      );
      setEditNoteId(null); // Exit edit mode
    } catch (error) {
      console.error("Error saving note:", error);
    }
  };

  const truncateContent = (content: string, maxLength: number = 15) =>
    content.length > maxLength ? `${content.slice(0, maxLength)}..` : content;

  useEffect(() => {
    fetchNotes();
  }, []);

  return (
    <Box
      sx={{
        position: "relative",
        width: "100%",
        height: "100vh",
        overflow: "hidden",
      }}
    >
      {/* Background Image */}
      <Box
        sx={{
          position: "absolute",
          top: 0,
          left: 0,
          width: "100%",
          height: "100%",
          backgroundImage: "url(/assets/images/bg-img.png)",
          backgroundSize: "cover",
          backgroundPosition: "center",
          opacity: 0.9,
          zIndex: -5,
        }}
      />

      {/* Main Content */}
      <Box
        sx={{
          p: 2,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          height: "100%",
          backgroundColor: "rgba(255, 255, 255, 0.5)", // Optional for readability
        }}
      >
        {/* Top Bar */}
        <Box
          sx={{
            width: "100%",
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            mb: 2,
          }}
        >
          <Typography variant="h6" sx={{ fontWeight: "bold" }}>
            Dashboard
          </Typography>
          <Button variant="text" color="primary" href="/logout">
            Sign Out
          </Button>
        </Box>

        {/* Notes List */}
        <Box
          sx={{
            width: "100%",
            maxWidth: 500,
            flexGrow: 1,
            overflowY: "auto",
          }}
        >
          <Typography variant="h6" gutterBottom>
            Notes
          </Typography>
          <Grid container spacing={2}>
            {notes.map((note) => (
              <Grid item xs={12} key={note._id}>
                <Card
                  sx={{
                    boxShadow: 1,
                    borderRadius: "8px",
                    p: 1.5,
                    backgroundColor: "#fff",
                  }}
                >
                  {editNoteId === note._id ? (
                    // Edit Mode
                    <CardContent>
                      <TextField
                        fullWidth
                        variant="outlined"
                        label="Title"
                        value={editedContent.title}
                        onChange={(e) =>
                          setEditedContent((prev) => ({
                            ...prev,
                            title: e.target.value,
                          }))
                        }
                        sx={{ mb: 2 }}
                      />
                      <TextField
                        fullWidth
                        variant="outlined"
                        label="Content"
                        multiline
                        minRows={3}
                        value={editedContent.content}
                        onChange={(e) =>
                          setEditedContent((prev) => ({
                            ...prev,
                            content: e.target.value,
                          }))
                        }
                        sx={{ mb: 2 }}
                      />
                      <Box
                        sx={{
                          display: "flex",
                          justifyContent: "space-between",
                        }}
                      >
                        <Button
                          variant="contained"
                          color="primary"
                          startIcon={<Save />}
                          onClick={() => handleSave(note._id)}
                        >
                          Save
                        </Button>
                        <Button
                          variant="text"
                          color="secondary"
                          onClick={() => setEditNoteId(null)}
                        >
                          Cancel
                        </Button>
                      </Box>
                    </CardContent>
                  ) : (
                    // View Mode
                    <CardContent
                      sx={{
                        display: "flex",
                        justifyContent: "space-between",
                        alignItems: "center",
                      }}
                    >
                      <Box>
                        <Typography variant="h6" fontWeight="bold">
                          {note.title}
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                          {truncateContent(note.content)}
                        </Typography>
                      </Box>
                      <Box>
                        <IconButton
                          aria-label="edit"
                          onClick={() => handleEdit(note)}
                        >
                          <Edit />
                        </IconButton>
                        <IconButton
                          aria-label="delete"
                          onClick={() => handleDelete(note._id)}
                        >
                          <Delete />
                        </IconButton>
                      </Box>
                    </CardContent>
                  )}
                </Card>
              </Grid>
            ))}
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default Dashboard;
